INSERT INTO exercises (name, description, type, main_muscle, equipment, level, rating, instructions, images_url, muscle_diagram_url)
    VALUES ('Chest dip', 'The chest dip is a bodyweight exercise performed on parallel bars or on a pull-up and dip station. It targets the chest, triceps, and shoulders. Dips with a chest focus are usually performed with the torso leaning forward and the elbows angled out from the torso. Dips can be performed for low reps for strength or higher reps for muscle growth.', 'Strength', 
    'Chest', 'Other', 'Level: Intermediate', '9',
    '1. For this exercise you will need access to parallel bars. To get yourself into the starting position, hold your body at arms length (arms locked) above the bars.
2. While breathing in, lower yourself slowly with your torso leaning forward around 30 degrees or so and your elbows flared out slightly until you feel a slight stretch in the chest.
3. Once you feel the stretch, use your chest to bring your body back to the starting position as you breathe out. Tip: Remember to squeeze the chest at the top of the movement for a second.
4. Repeat the movement for the prescribed amount of repetitions.', 'https://www.bodybuilding.com/images/2020/xdb/originals/xdb-2m-chest-dip-m1-16x9.jpg, https://www.bodybuilding.com/images/2020/xdb/originals/xdb-2m-chest-dip-m2-16x9.jpg', 'https://artifacts.bbcomcdn.com/@bbcom/exercises-app/2.1.2/img/guide-1.gif');
    
INSERT INTO exercises (name, description, type, main_muscle, equipment, level, rating, instructions, images_url, muscle_diagram_url)
    VALUES ('Chest Push (multiple response)', 'n/a', 'Plyometrics', 
