INSERT INTO exercises (name, description, type, main_muscle, equipment, level, rating, instructions, images_url, muscle_diagram_url)
    VALUES ('Child''s pose', 'Childs pose is a traditional yoga pose that is also popular in fitness and strength training. It targets mobility of the thoracic spine and control of the vertebral column. It can be performed for time or reps as part of a dynamic warm-up or as a mobility or restorative exercise anytime. It can also work as part of a yoga or bodyweight sequence with other poses.', 'Stretching', 
    'Lower Back', 'Body Only', 'Level: Intermediate', '5.3',
    '1. Get on your hands and knees, walk your hands in front of you.
2. Lower your buttocks down to sit on your heels. Let your arms drag along the floor as you sit back to stretch your entire spine.
3. Once you settle onto your heels, bring your hands next to your feet and relax. "breathe" into your back. Rest your forehead on the floor. Avoid this position if you have knee problems.', 'https://www.bodybuilding.com/images/2020/xdb/originals/xdb-232a-childs-pose-m1-16x9.jpg, https://www.bodybuilding.com/images/2020/xdb/originals/xdb-232a-childs-pose-m2-16x9.jpg', 'https://artifacts.bbcomcdn.com/@bbcom/exercises-app/2.1.2/img/guide-5.gif');
    
INSERT INTO exercises (name, description, type, main_muscle, equipment, level, rating, instructions, images_url, muscle_diagram_url)
    VALUES ('Chin To Chest Stretch', 'n/a', 'Stretching', 
