INSERT INTO exercises (name, description, type, main_muscle, equipment, level, rating, instructions, images_url, muscle_diagram_url)
    VALUES ('Two-Arm Kettlebell Jerk', 'n/a', 'Strength', 
    'Shoulders', 'Kettlebells', 'Level: Beginner', '0',
    '1. Clean two kettlebells to your shoulders. Clean the kettlebells to your shoulders by extending through the legs and hips as you swing the kettlebells towards your shoulders. Rotate your wrists as you do so, so that the palms face forward. Squat down a few inches and reverse the motion rapidly driving both kettlebells overhead. Immediately after the initial push, squat down again and get under the kettlebells. Once the kettlebells are locked out, stand upright to complete the exercise.', 'https://www.bodybuilding.com/exercises/exerciseImages/sequences/528/Male/l/528_1.jpg, https://www.bodybuilding.com/exercises/exerciseImages/sequences/528/Male/l/528_2.jpg', 'https://artifacts.bbcomcdn.com/@bbcom/exercises-app/2.1.2/img/guide-12.gif');
    
INSERT INTO exercises (name, description, type, main_muscle, equipment, level, rating, instructions, images_url, muscle_diagram_url)
    VALUES ('Two-Arm Kettlebell Military Press', 'n/a', 'Strength', 
    'Shoulders', 'Kettlebells', 'Level: Beginner', '8.7',
